uint16_t      base_layer = 0;
layer_state_t default_layer_state_set_user(layer_state_t state) { // manage locked layer labels
    switch (state) {
        case 0:
        case 1:
            base_layer = BASE;
            break;
        case 2:
            base_layer = EXTRA;
            break;
        case 4:
            base_layer = TAP;
            break;
        case 16:
            base_layer = NAV;
            break;
        case 32:
            base_layer = MOUSE;
            break;
        case 64:
            base_layer = MEDIA;
            break;
        case 128:
            base_layer = NUM;
            break;
        case 256:
            base_layer = SYM;
            break;
        case 512:
            base_layer = FUN;
            break;
        default:
            base_layer = UNKNOWN;
    }
    return state;
}

void render_label() {
    switch (get_highest_layer(layer_state)) {
        case BASE:
            oled_write_raw_P(LAYER_LABELS[base_layer], LAYER_LABEL_SIZE);
            break;
        case BUTTON:
            oled_write_raw_P(LAYER_LABELS[BUTTON], LAYER_LABEL_SIZE);
            break;
        case NAV:
            oled_write_raw_P(LAYER_LABELS[NAV], LAYER_LABEL_SIZE);
            break;
        case MOUSE:
            oled_write_raw_P(LAYER_LABELS[MOUSE], LAYER_LABEL_SIZE);
            break;
        case MEDIA:
            oled_write_raw_P(LAYER_LABELS[MEDIA], LAYER_LABEL_SIZE);
            break;
        case NUM:
            oled_write_raw_P(LAYER_LABELS[NUM], LAYER_LABEL_SIZE);
            break;
        case SYM:
            oled_write_raw_P(LAYER_LABELS[SYM], LAYER_LABEL_SIZE);
            break;
        case FUN:
            oled_write_raw_P(LAYER_LABELS[FUN], LAYER_LABEL_SIZE);
            break;
        default:
            oled_write_raw_P(LAYER_LABELS[UNKNOWN], LAYER_LABEL_SIZE);
            break;
    }
}

